import { v4 as uuidv4 } from "uuid";
import {link} from "../../localhost";
import {ADD_TODO, DELETE_ALL_TODO_API, DELETE_TODO, GET_TODOS_SUCCESS, TOGGLE_TODO, TOGGLE_TODO_API} from "./types";

export const toggleTodoAction = (todo) => ({
  type: "TOGGLE_TODO",
  id: todo.id,
});

export const deleteTodoAction = (todo) => ({
  type: "DELETE_TODO",
  id: todo.id,
});

export const addTodoAction = (inputValue) => ({
  type: "ADD_TODO",
  id: uuidv4(),
  value: inputValue,
});
export const deleteAllTodo = (todos) => ({
  type: "DELETE_ALL_TODO",
  id: todos.id,
});

export const fetchTodos = () => async (dispatch,state,api) =>{
  dispatch({
    type: "START_LOADING_TODOS",
  })
  return api.fetchData().then(response =>{
    console.log('loading')

    dispatch({
      type:GET_TODOS_SUCCESS,
      payload:response
    })
  }).catch(err =>dispatch({
    type:'ERROR_IN_LOADING_TODOS'
  }))
}
export const addTodoAPI = (inputValue) => async (dispatch, state, api) =>{
  dispatch({
    type: 'START_ADD_TODO'
  })
  return api.createNewTodo(inputValue).then(response => {
    console.log(response)

    dispatch({
      type: ADD_TODO,
      payload: response
    })
  }).catch(err => dispatch({
    type: 'ERROR_IN_ADD_TODO'
  }))
}
export const deleteTodoAPI = (todo) => async (dispatch,state,api) => {
  dispatch({
    type:'START_DELETE_TODO'
  })
  return api.deleteTodo(todo).then(response => {
    console.log(todo.id)

    dispatch({
      type:DELETE_TODO,
      payload:todo.id
    })
  }).catch(err=>  dispatch({
    type:'ERROR_IN_DELETING_TODO'
  }))
}

export const toggleTodoAPI = (elem) => async (dispatch,state,api) =>{
  dispatch({
    type:'START_TOGGLE_TODO'
  })
  return api.listItemToggle(elem).then(response =>{
    console.log(elem)

    dispatch({
      type:TOGGLE_TODO,
      payload:elem.id
    })
  })
}

export const deleteAllTodoAPI = () => async  (dispatch) =>{
  dispatch({
    type:DELETE_ALL_TODO_API,
    payload:fetch(link, {
      method: "DELETE",
    })
  })
}
