import { v4 as uuidv4 } from "uuid";
import {link} from "../../localhost";

export const toggleTodoAction = (todo) => ({
  type: "TOGGLE_TODO",
  id: todo.id,
});

export const deleteTodoAction = (todo) => ({
  type: "DELETE_TODO",
  id: todo.id,
});

export const addTodoAction = (inputValue) => ({
  type: "ADD_TODO",
  id: uuidv4(),
  value: inputValue,
});
export const deleteAllTodo = (todos) => ({
  type: "DELETE_ALL_TODO",
  id: todos.id,
});

export const fetchTodos = () => async (dispatch) =>{
  // fetch(link).then(res)
  // TODO: replace await with then
  const response = await  fetch(link);
  const todos = await response.json();

  dispatch({
    type: "GET_TODOS_SUCCESS",
    payload: todos
  })
}
export const addTodoAPI = (inputValue) => async (dispatch) =>{
  dispatch({
    type:"ADD_NEW_TODO_API",
    payload: fetch(link,{
      method:"POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ value: inputValue }),
    })
  })
}
export const deleteTodoAPI = (todo) => async (dispatch) => {
  dispatch({
    type: "ADD_NEW_TODO_API",
    payload: fetch(`${link}${todo.id}`, {
      method: "DELETE",
    })
  })
}
export const test = (todos) => (dispatch) => {
  // dispatch({type: 'deleting-todo'});
  // fetch('/delete').then((res) => {
  //   dispatch({type: 'deleting-todo-success'});
  // })
  new Promise((resolve) => {
    setTimeout(() => resolve(), 5000);
  }).then(() => {
    console.log('resolved');
    dispatch({
      type: "DELETE_ALL_TODO",
          id: todos.id,
    })
  })
}

export const getTodosFromAPI =() =>{
  return function (dispatch)  {
         const fetchData = async () =>{
           const response = await fetch(link)
           let todo = await response.json()
           console.log(todo)
         }
         fetchData()
    };

  }
