import { v4 as uuidv4 } from "uuid";
import {link} from "../../localhost";
import {DELETE_ALL_TODO_API, TOGGLE_TODO_API} from "./types";

export const toggleTodoAction = (todo) => ({
  type: "TOGGLE_TODO",
  id: todo.id,
});

export const deleteTodoAction = (todo) => ({
  type: "DELETE_TODO",
  id: todo.id,
});

export const addTodoAction = (inputValue) => ({
  type: "ADD_TODO",
  id: uuidv4(),
  value: inputValue,
});
export const deleteAllTodo = (todos) => ({
  type: "DELETE_ALL_TODO",
  id: todos.id,
});

export const fetchTodos = () => async (dispatch) =>{
  // fetch(link).then(res)
  // TODO: replace await with then
  const response = await  fetch(link);
  const todos = await response.json();

  dispatch({
    type: "GET_TODOS_SUCCESS",
    payload: todos
  })
}
export const addTodoAPI = (inputValue) => async (dispatch, state, api) =>{
  /*dispatch({
    type:"add_new_todo_api",
    payload: fetch(link,{
      method:"post",
      headers: { "content-type": "application/json" },
      body: json.stringify({ value: inputvalue }),
    })
  }*/
  dispatch({
    type: 'START_ADD_TODO'
  })

  return api.createNewTodo(inputValue).then(response => {
    console.log('response')

    dispatch({
      type: 'ADD_TODO',
      payload: response
    })
  }).catch(err => dispatch({
    type: 'ERROR_IN_ADD_TODO'
  }))
}
export const deleteTodoAPI = (todo) => async (dispatch) => {
  dispatch({
    type: "DELETE_TODO_API",
    payload: fetch(`${link}${todo.id}`, {
      method: "DELETE",
    })
  })
}

export const deleteAllTodoAPI = () => async  (dispatch) =>{
    dispatch({
      type:DELETE_ALL_TODO_API,
      payload:fetch(link, {
        method: "DELETE",
      })
    })
}
export const toggleTodoAPI = (elem) => async (dispatch) =>{
  dispatch({
    type:TOGGLE_TODO_API,
    payload:fetch(`${link}/toggle/${elem.id}`, {
      method: "PUT",
    })
  })
}
