import { v4 as uuidv4 } from "uuid";
import {
  ADD_TODO,
  DELETE_TODO,
  TOGGLE_TODO,
  DELETE_ALL_TODO,
  GET_TODOS_SUCCESS,
  ADD_NEW_TODO_API,
  DELETE_TODO_API, DELETE_ALL_TODO_API,TOGGLE_TODO_API
} from "./types";
const todo = (state, action) => {
  switch (action.type) {
    case ADD_TODO:
      return {
        id: action.id,
        value: action.value,
        isDone: false,
      };
    case DELETE_TODO:
      if (state.id !== action.id) {
        return state;
      }
      return {
        ...state,
      };
    case DELETE_ALL_TODO:
      if (state.id !== uuidv4()) {
        return state;
      }
      return {
        ...state,
      };
    case TOGGLE_TODO:
      if (state.id !== action.id) {
        return state;
      }
      return {
        ...state,
        isDone: !state.isDone,
      };
    default:
      return state;
  }
};

let todoList =[]



export const todos = (state = todoList, action) => {
  switch (action.type) {
    case ADD_TODO:
      console.log(todo(undefined,action))
        console.log(state)
      return [...state, todo(undefined, action)];
    case DELETE_TODO:
      return state.filter((t) => t.id !== action.id);
    case TOGGLE_TODO:
      return state.map((t) => todo(t, action));
    case DELETE_ALL_TODO:
      return state.filter((t) => t.id === uuidv4());
    case GET_TODOS_SUCCESS:
      return action.payload;
    case ADD_NEW_TODO_API:
      return action.payload
    case DELETE_TODO_API:
      return action.payload
    case DELETE_ALL_TODO_API:
      return action.payload
    case TOGGLE_TODO_API:
      return action.payload
    default:
      return state;
  }
};
